mapper.select:
select POST_CON 
	from POST_TBL 
	where POST_IDX = #{post_idx};

mapper.update:
update from POST_TBL 
	set POST_TTL = #{post_ttl}, 
		POST_DATE = SYSDATE(), 
		POST_CON = #{post_con} 
	where POST_IDX = #{post_idx};

mapper.insert:
insert into RECM_TBL(POST_IDX, MEM_ID) 
	select #{post_idx}, 
		   #{mem_id} 
		from DUAL 
	where not exists 
		(select * 
			from RECM_TBL 
			where POST_IDX = #{post_idx} 
				and MEM_ID = #{mem_id});
