mapper.select:
select * from SCHE_TBL 
	where STAR_IDX = #{star_idx} 
		and SCHE_DATE >= DATE_FORMAT(#{SCHE_DATE}, '%Y/%m/01') 
		and SCHE_DATE < DATE_FORMAT(#{SCHE_DATE} + INTERVAL 1 MONTH, '%Y/%m/01');

mapper.update:
update from SCHE_TBL 
	set SCHE_NM = #{sche_nm}, 
		SCHE_DATE = #{sche_date}, 
		SCHE_TRM = #{sche_trm} 
	where SCHE_IDX = #{sche_idx};
