mapper.update:
update MEM_TBL M 
	LEFT OUTER JOIN FILE_TBL F 
		ON M.FILE_IDX = F.FILE_IDX 
	SET M.MEM_PWD = hex(aes_encrypt(#{mem_pwd}, #{pwd_key})), 
		M.MEM_NICK = #{mem_nick}, 
		M.MEM_NM = #{mem_nm}, 
		F.FILE_NM = #{file_nm}
		F.FILE_PATH = #{file_path} 
	where M.MEM_ID = #{mem_id};

update MEM_TBL 
	set ACVT = 0 
	where MEM_ID = #{mem_id};

update MEM_TBL 
	set MEM_STAT = 1 
	where MEM_ID = #{mem_id};
