mapper.insert:
start transaction; insert into FILE_TBL(FILE_NM, FILE_PATH, FILE_CRT_DATE) values (#{file_nm}, #{file_path}, SYSDATE());
insert into MEM_TBL (MEM_ID, MEM_PWD, MEM_NICK, MEM_NM, FILE_IDX) values (#{mem_id}, hex(aes_encrypt(#{mem_pwd}, #{pwd_key})), #{mem_nick}, #{mem_nm}, LAST_INSERT_ID());
commit;

mapper.update:
update MEM_TBL 
	set ACVT = 0 
	where MEM_ID = #{mem_id};

update MEM_TBL 
	set MEM_STAT = #{mem_stat} 
	where MEM_ID = #{mem_id};
